package com.bank.util;
import java.math.BigDecimal;
public final class BankExceptions { private BankExceptions() {} public static class InsufficientFunds extends RuntimeException { public InsufficientFunds(String accountNo) { super("Insufficient funds in account " + accountNo); } public InsufficientFunds(String accountNo, String msg) { super(msg + " [account " + accountNo + "]"); } } public static class InvalidOperation extends RuntimeException { public InvalidOperation(String msg) { super(msg); } } public static class MinimumOpeningDeposit extends RuntimeException { public MinimumOpeningDeposit(BigDecimal min) { super("Minimum opening deposit is " + min); } } public static class AccountNotFound extends RuntimeException { public AccountNotFound(String accountNo) { super("Account not found: " + accountNo); } } }
